import { Component, OnInit } from '@angular/core';
import { LoginService } from '../login.service';
import { Store } from '@ngrx/store';
import { AuthState } from '../store/auth/auth.reducer';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
})
export class LoginComponent implements OnInit {
  username: string = '';
  password: string = '';
  email: string = '';
  showLogin: boolean = true;

  constructor(private api: LoginService, private store: Store<AuthState>) {}

  ngOnInit(): void {}

  login(): void {
    this.api.login(this.username, this.password).subscribe({
      next: () => {
        console.log('Login successful');
      },
      error: (err) => {
        console.error('Login error:', err);
      },
    });
  }

  isLoggedIn(): boolean {
    return this.api.isAuthenticated();
  }

  logout(): void {
    this.api.logout();
  }

  register(): void {
    this.api.register(this.username, this.password, this.email).subscribe({
      next: (response) => {
        console.log(response.message);
        this.login();
      },
      error: (err) => {
        console.error('Registration error:', err);
      },
    });
  }
}
<nav class="navbar navbar-expand-lg navbar-light bg-black justify-between">
  <a class="navbar-brand text-white " href="#">Login</a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav"
    aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div class="collapse navbar-collapse" id="navbarNav">
    <ul class="navbar-nav ml-auto">
      <li class=" text-white" *ngIf="isLoggedIn()">
        <a class=" text-white" (click)="logout()">Logout</a>
      </li>
    </ul>
  </div>
</nav>



<nav class="bg-white border-gray-200 dark:bg-gray-900">
  <div class="max-w-screen-xl flex flex-wrap items-center justify-between mx-auto p-4">
    <a href="https://flowbite.com/" class="flex items-center space-x-3 rtl:space-x-reverse">
        <img src="https://flowbite.com/docs/images/logo.svg" class="h-8" alt="Flowbite Logo" />
        <span class="self-center text-2xl font-semibold whitespace-nowrap dark:text-white">Flowbite</span>
    </a>
    <button data-collapse-toggle="navbar-default" type="button" class="inline-flex items-center p-2 w-10 h-10 justify-center text-sm text-gray-500 rounded-lg md:hidden hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200 dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600" aria-controls="navbar-default" aria-expanded="false">
        <span class="sr-only">Open main menu</span>
        <svg class="w-5 h-5" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 17 14">
            <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M1 1h15M1 7h15M1 13h15"/>
        </svg>
    </button>
    <div class="hidden w-full md:block md:w-auto" id="navbar-default">
      <ul class="font-medium flex flex-col p-4 md:p-0 mt-4 border border-gray-100 rounded-lg bg-gray-50 md:flex-row md:space-x-8 rtl:space-x-reverse md:mt-0 md:border-0 md:bg-white dark:bg-gray-800 md:dark:bg-gray-900 dark:border-gray-700">
        <li>
          <a href="#" class="block py-2 px-3 text-white bg-blue-700 rounded md:bg-transparent md:text-blue-700 md:p-0 dark:text-white md:dark:text-blue-500" aria-current="page" (click)="logout()">Logout</a>
        </li>

      </ul>
    </div>
  </div>
</nav>


<div class="container mx-auto mt-20 p-6 bg-gray-800 text-white rounded drop-shadow-lg">
  <div class="flex justify-center mb-6">
    <button class="px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded" (click)="showLogin = true">Login</button>
    <button class="ml-4 px-4 py-2 bg-green-500 hover:bg-green-600 text-white rounded" (click)="showLogin = false">Register</button>
  </div>

  <div *ngIf="showLogin">
    <h2 class="text-2xl mb-6">Login</h2>
    <form (ngSubmit)="login()">
      <div class="mb-4">
        <label for="username" class="block mb-1">Username:</label>
        <input type="text" id="username" name="username" [(ngModel)]="username" class="w-full p-2 rounded bg-gray-900 border border-gray-700" required>
      </div>
      <div class="mb-6">
        <label for="password" class="block mb-1">Password:</label>
        <input type="password" id="password" name="password" [(ngModel)]="password" class="w-full p-2 rounded bg-gray-900 border border-gray-700" required>
      </div>
      <button type="submit" class="w-full py-2 bg-blue-500 hover:bg-blue-600 rounded">Login</button>
    </form>
  </div>

  <div *ngIf="!showLogin">
    <h2 class="text-2xl mb-6">Register</h2>
    <form (ngSubmit)="register()">
      <div class="mb-4">
        <label for="username" class="block mb-1">Username:</label>
        <input type="text" id="username" name="username" [(ngModel)]="username" class="w-full p-2 rounded bg-gray-900 border border-gray-700" required>
      </div>
      <div class="mb-4">
        <label for="password" class="block mb-1">Password:</label>
        <input type="password" id="password" name="password" [(ngModel)]="password" class="w-full p-2 rounded bg-gray-900 border border-gray-700" required>
      </div>
      <div class="mb-6">
        <label for="email" class="block mb-1">Email:</label>
        <input type="email" id="email" name="email" [(ngModel)]="email" class="w-full p-2 rounded bg-gray-900 border border-gray-700" required>
      </div>
      <button type="submit" class="w-full py-2 bg-green-500 hover:bg-green-600 rounded">Register</button>
    </form>
  </div>
</div>
