import { createAction, props } from '@ngrx/store';

export const login = createAction(
  '[Auth] Login',
  props<{ username: string; password: string }>()
);

export const addToken = createAction(
  '[Auth] Add Token',
  props<{ token: string }>()
);

/* xport const loginSuccess = createAction(
  '[Auth] Login Success',
  props<{ token: string }>()
); */
export const loginSuccess = createAction('[loginSuccess] Set,  props<{ token: string }>()');

export const loginFailure = createAction(
  '[Auth] Login Failure',
  props<{ error: any }>()
);

export const removeToken = createAction('[Auth] Remove Token');
export const logout = createAction('[Auth] Logout');
